import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { registerUser, updateUserStep, getAdminConfig } from './api';
import AboutMeComponent from './components/form-components/AboutMeComponent';
import AddressComponent from './components/form-components/AddressComponent';
import BirthdateComponent from './components/form-components/BirthdateComponent';
import AdminDashboard from './components/AdminDashboard';
import DataTable from './components/DataTable';
import './App.css';

// Main Onboarding Component
function OnboardingWizard() {
  const [currentStep, setCurrentStep] = useState(1);
  const [userId, setUserId] = useState(null);
  const [userData, setUserData] = useState({});
  const [adminConfig, setAdminConfig] = useState({ 2: [], 3: [] });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadAdminConfig();
    checkExistingUser();
  }, []);

  const loadAdminConfig = async () => {
    try {
      const config = await getAdminConfig();
      setAdminConfig(config);
    } catch (error) {
      console.error('Failed to load admin config:', error);
    }
  };

  const checkExistingUser = () => {
    const savedUserId = localStorage.getItem('zealthy_user_id');
    const savedStep = localStorage.getItem('zealthy_current_step');
    if (savedUserId && savedStep) {
      setUserId(parseInt(savedUserId));
      setCurrentStep(parseInt(savedStep));
    }
  };

  const handleStep1Submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    const formData = new FormData(e.target);
    const newUserData = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await registerUser(newUserData);
      setUserId(response.id);
      setCurrentStep(2);

      // Save progress to localStorage
      localStorage.setItem('zealthy_user_id', response.id);
      localStorage.setItem('zealthy_current_step', '2');

      alert(`âœ… Registration successful! User ID: ${response.id}. Moving to Step 2.`);
    } catch (error) {
      setError('Registration failed. Email might already exist.');
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStep1Save = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');

    const formData = new FormData(e.target);
    const newUserData = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await registerUser(newUserData);
      setUserId(response.id);
      // Stay on Step 1 but save user ID
      localStorage.setItem('zealthy_user_id', response.id);
      localStorage.setItem('zealthy_current_step', '1');

      alert(`âœ… Account created and saved! User ID: ${response.id}. You're still on Step 1.`);
    } catch (error) {
      setError('Registration failed. Email might already exist.');
      console.error('Registration error:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleDataChange = (field, value) => {
    setUserData(prev => ({ ...prev, [field]: value }));
  };

  const handleSaveData = async () => {
    setSaving(true);
    try {
      // Save data but keep current step the same
      await updateUserStep(userId, currentStep, userData);
      alert(`âœ… Data saved successfully! You're still on Step ${currentStep}.`);
    } catch (error) {
      alert('âŒ Failed to save data');
      console.error('Save error:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleNextStep = async () => {
    if (currentStep === 2) {
      // Save step 2 data and move to step 3
      try {
        await updateUserStep(userId, 2, userData);

        // Update current step to 3 in database
        await fetch(`http://localhost:8080/api/users/${userId}/step/3`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({})
        });

        setCurrentStep(3);
        localStorage.setItem('zealthy_current_step', '3');
        alert('âœ… Step 2 completed! Moving to Step 3.');
      } catch (error) {
        alert('Failed to save data');
        console.error('Update error:', error);
      }
    } else if (currentStep === 3) {
      // Save step 3 data and complete (stay on Step 3)
      try {
        await updateUserStep(userId, 3, userData);

        // User stays on Step 3 - no Step 4!
        alert('ðŸŽ‰ Registration Complete! You completed all steps. Check the data table to see your info.');
        localStorage.removeItem('zealthy_user_id');
        localStorage.removeItem('zealthy_current_step');
        setCurrentStep(1);
        setUserId(null);
        setUserData({});
      } catch (error) {
        alert('Failed to complete registration');
        console.error('Complete error:', error);
      }
    }
  };

  const renderComponents = (pageNumber) => {
    const components = adminConfig[pageNumber] || [];

    return components.map(componentType => {
      switch (componentType) {
        case 'ABOUT_ME':
          return (
            <AboutMeComponent
              key="about-me"
              value={userData.aboutMe}
              onChange={handleDataChange}
            />
          );
        case 'ADDRESS':
          return (
            <AddressComponent
              key="address"
              values={userData}
              onChange={handleDataChange}
            />
          );
        case 'BIRTHDATE':
          return (
            <BirthdateComponent
              key="birthdate"
              value={userData.birthdate}
              onChange={handleDataChange}
            />
          );
        default:
          return null;
      }
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸŽ¯ Zealthy Onboarding</h1>

        {/* Progress Bar */}
        <div className="progress-bar" style={{ 
          display: 'flex', 
          gap: '20px', 
          marginBottom: '30px' 
        }}>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 1 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>1</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 2 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>2</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 3 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>3</div>
        </div>

        {/* Current Step Info */}
        {userId && (
          <div style={{
            backgroundColor: '#e3f2fd',
            padding: '10px 20px',
            borderRadius: '5px',
            marginBottom: '20px',
            color: '#1976d2'
          }}>
            <strong>User ID:</strong> {userId} | <strong>Current Step:</strong> {currentStep}
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div style={{ 
            color: '#dc3545', 
            backgroundColor: '#f8d7da', 
            padding: '10px', 
            borderRadius: '5px', 
            marginBottom: '20px' 
          }}>
            {error}
          </div>
        )}

        {/* Step 1: Email/Password */}
        {currentStep === 1 && (
          <div className="step-content">
            <h2>Step 1: Create Account</h2>
            <form style={{ maxWidth: '400px', margin: '0 auto' }}>
              <div style={{ marginBottom: '15px' }}>
                <input 
                  type="email" 
                  name="email"
                  placeholder="Email" 
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>
              <div style={{ marginBottom: '20px' }}>
                <input 
                  type="password" 
                  name="password"
                  placeholder="Password (min 6 characters)" 
                  minLength="6"
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>

              {/* Button Group */}
              <div style={{ 
                display: 'flex', 
                gap: '10px',
                flexDirection: 'column'
              }}>
                <button 
                  type="button"
                  onClick={handleStep1Save}
                  disabled={saving}
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    backgroundColor: saving ? '#6c757d' : '#17a2b8',
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '5px',
                    fontSize: '16px',
                    cursor: saving ? 'not-allowed' : 'pointer'
                  }}
                >
                  {saving ? 'ðŸ’¾ Saving...' : 'ðŸ’¾ Save Account (Stay on Step 1)'}
                </button>

                <button 
                  type="button"
                  onClick={handleStep1Submit}
                  disabled={loading}
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    backgroundColor: loading ? '#ccc' : '#007bff',
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '5px',
                    fontSize: '16px',
                    cursor: loading ? 'not-allowed' : 'pointer'
                  }}
                >
                  {loading ? 'Creating Account...' : 'Save & Next Step (Go to Step 2) â†’'}
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Step 2: Dynamic Components */}
        {currentStep === 2 && (
          <div className="step-content">
            <h2>Step 2: Tell Us About Yourself</h2>

            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '30px', 
              maxWidth: '600px', 
              margin: '20px auto' 
            }}>
              {renderComponents(2)}
            </div>

            {adminConfig[2]?.length === 0 && (
              <p style={{ color: '#666' }}>No components configured for this step.</p>
            )}

            {/* Button Group */}
            <div style={{ 
              display: 'flex', 
              gap: '15px', 
              justifyContent: 'center',
              marginTop: '20px' 
            }}>
              <button 
                onClick={handleSaveData}
                disabled={saving}
                style={{ 
                  padding: '12px 24px', 
                  backgroundColor: saving ? '#6c757d' : '#17a2b8',
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '5px',
                  fontSize: '16px',
                  cursor: saving ? 'not-allowed' : 'pointer'
                }}
              >
                {saving ? 'ðŸ’¾ Saving...' : 'ðŸ’¾ Save (Stay on Step 2)'}
              </button>

              <button 
                onClick={handleNextStep}
                style={{ 
                  padding: '12px 24px', 
                  backgroundColor: '#28a745', 
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '5px',
                  fontSize: '16px',
                  cursor: 'pointer'
                }}
              >
                Save & Next Step (Go to Step 3) â†’
              </button>
            </div>
          </div>
        )}

        {/* Step 3: Dynamic Components */}
        {currentStep === 3 && (
          <div className="step-content">
            <h2>Step 3: Almost Done!</h2>

            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '30px', 
              maxWidth: '600px', 
              margin: '20px auto' 
            }}>
              {renderComponents(3)}
            </div>

            {adminConfig[3]?.length === 0 && (
              <p style={{ color: '#666' }}>No components configured for this step.</p>
            )}

            {/* Button Group */}
            <div style={{ 
              display: 'flex', 
              gap: '15px', 
              justifyContent: 'center',
              marginTop: '20px' 
            }}>
              <button 
                onClick={handleSaveData}
                disabled={saving}
                style={{ 
                  padding: '12px 24px', 
                  backgroundColor: saving ? '#6c757d' : '#17a2b8',
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '5px',
                  fontSize: '16px',
                  cursor: saving ? 'not-allowed' : 'pointer'
                }}
              >
                {saving ? 'ðŸ’¾ Saving...' : 'ðŸ’¾ Save (Stay on Step 3)'}
              </button>

              <button 
                onClick={handleNextStep}
                style={{ 
                  padding: '12px 24px', 
                  backgroundColor: '#dc3545', 
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '5px',
                  fontSize: '16px',
                  cursor: 'pointer'
                }}
              >
                Save & Complete (Finish All Steps!) ðŸŽ‰
              </button>
            </div>
          </div>
        )}

        {/* Navigation */}
        <div style={{ marginTop: '40px' }}>
          <nav>
            <Link to="/admin" style={{ color: '#007bff', textDecoration: 'none' }}>
              ðŸ”§ Admin Dashboard
            </Link>
            {' | '}
            <Link to="/data" style={{ color: '#007bff', textDecoration: 'none' }}>
              ðŸ“Š View Data
            </Link>
          </nav>
        </div>
      </header>
    </div>
  );
}

