import React, { useState, useEffect } from 'react';
import { getAdminConfig } from '../api';

function AdminDashboard() {
  const [config, setConfig] = useState({ 2: [], 3: [] });
  const [loading, setLoading] = useState(true);

  const components = [
    { key: 'ABOUT_ME', name: 'About Me', icon: 'üìù' },
    { key: 'ADDRESS', name: 'Address', icon: 'üè†' },
    { key: 'BIRTHDATE', name: 'Birthdate', icon: 'üéÇ' }
  ];

  useEffect(() => {
    loadConfig();
  }, []);

  const loadConfig = async () => {
    try {
      const response = await getAdminConfig();
      setConfig(response);
    } catch (error) {
      console.error('Failed to load config:', error);
    } finally {
      setLoading(false);
    }
  };

  const moveComponent = (componentKey, newPage) => {
    const newConfig = { ...config };
    
    // Remove component from all pages
    Object.keys(newConfig).forEach(page => {
      newConfig[page] = newConfig[page].filter(comp => comp !== componentKey);
    });
    
    // Add to new page
    newConfig[newPage] = [...newConfig[newPage], componentKey];
    
    setConfig(newConfig);
  };

  const saveConfig = async () => {
    try {
      const componentPageMap = {};
      Object.entries(config).forEach(([page, components]) => {
        components.forEach(comp => {
          componentPageMap[comp] = parseInt(page);
        });
      });

      const response = await fetch('http://localhost:8080/api/admin/config', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ componentPageMap })
      });

      if (response.ok) {
        alert('‚úÖ Configuration saved successfully!');
      } else {
        alert('‚ùå Failed to save configuration');
      }
    } catch (error) {
      alert('‚ùå Error saving configuration');
      console.error('Save error:', error);
    }
  };

  if (loading) return <div style={{ padding: '20px' }}>Loading...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1>üîß Admin Dashboard</h1>
      <p>Configure which components appear on each page of the onboarding flow.</p>
      
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '30px', marginTop: '30px' }}>
        
        {/* Page 2 */}
        <div style={{ border: '2px solid #007bff', borderRadius: '10px', padding: '20px' }}>
          <h2>üìÑ Page 2</h2>
          <div style={{ minHeight: '150px', backgroundColor: '#f8f9fa', borderRadius: '5px', padding: '15px' }}>
            {config[2]?.length === 0 ? (
              <p style={{ color: '#666' }}>No components assigned</p>
            ) : (
              config[2]?.map(comp => {
                const component = components.find(c => c.key === comp);
                return (
                  <div key={comp} style={{ 
                    backgroundColor: 'white', 
                    padding: '10px', 
                    margin: '5px 0', 
                    borderRadius: '5px',
                    border: '1px solid #ddd'
                  }}>
                    {component?.icon} {component?.name}
                  </div>
                );
              })
            )}
          </div>
        </div>

        {/* Page 3 */}
        <div style={{ border: '2px solid #28a745', borderRadius: '10px', padding: '20px' }}>
          <h2>üìÑ Page 3</h2>
          <div style={{ minHeight: '150px', backgroundColor: '#f8f9fa', borderRadius: '5px', padding: '15px' }}>
            {config[3]?.length === 0 ? (
              <p style={{ color: '#666' }}>No components assigned</p>
            ) : (
              config[3]?.map(comp => {
                const component = components.find(c => c.key === comp);
                return (
                  <div key={comp} style={{ 
                    backgroundColor: 'white', 
                    padding: '10px', 
                    margin: '5px 0', 
                    borderRadius: '5px',
                    border: '1px solid #ddd'
                  }}>
                    {component?.icon} {component?.name}
                  </div>
                );
              })
            )}
          </div>
        </div>
      </div>

      {/* Component Controls */}
      <div style={{ marginTop: '30px' }}>
        <h3>Move Components:</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '15px' }}>
          {components.map(component => (
            <div key={component.key} style={{ 
              border: '1px solid #ddd', 
              borderRadius: '5px', 
              padding: '15px', 
              textAlign: 'center' 
            }}>
              <div style={{ fontSize: '24px', marginBottom: '10px' }}>{component.icon}</div>
              <div style={{ fontWeight: 'bold', marginBottom: '10px' }}>{component.name}</div>
              <div style={{ display: 'flex', gap: '5px', justifyContent: 'center' }}>
                <button 
                  onClick={() => moveComponent(component.key, 2)}
                  style={{ 
                    padding: '5px 10px', 
                    backgroundColor: '#007bff', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '3px',
                    cursor: 'pointer'
                  }}
                >
                   Page 2
                </button>
                <button 
                  onClick={() => moveComponent(component.key, 3)}
                  style={{ 
                    padding: '5px 10px', 
                    backgroundColor: '#28a745', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '3px',
                    cursor: 'pointer'
                  }}
                >
                   Page 3
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div style={{ marginTop: '30px', textAlign: 'center' }}>
        <button 
          onClick={saveConfig}
          style={{ 
            padding: '15px 30px', 
            backgroundColor: '#dc3545', 
            color: 'white', 
            border: 'none', 
            borderRadius: '5px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          üíæ Save Configuration
        </button>
      </div>

      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <a href="/" style={{ color: '#007bff' }}>‚Üê Back to Onboarding</a> | 
        <a href="/data" style={{ color: '#007bff' }}> View Data ‚Üí</a>
      </div>
    </div>
  );
}

export default AdminDashboard;
